$(function() {
    altair_login_page.init();
    altair_login_page.altair_form_validation();
});
var $login_card = $("#login_card"),
$login_form = $("#login_form"),
$login_help = $("#login_help"),
$register_form = $("#register_form"),
$login_password_reset = $("#login_password_reset");
altair_login_page = {
	altair_form_validation: function() {
	    var i = $("#loginForm");
        var remberMe = $("#remberMe").val();
        if (remberMe){
            $.cookie("username", $("#username").val());
            $.cookie("password", $("#password").val());
        }

	    i.parsley().on("form:validated",
	    function() {
	        altair_md.update_input(i.find(".md-input-danger"))
	    }).on("field:validated",
	    function(i) {
	        $(i.$element).hasClass("md-input") && altair_md.update_input($(i.$element))
	    })
	    
	    var i = $("#inputForm");
            
	    i.parsley().on("form:validated",
	    function() {
	        altair_md.update_input(i.find(".md-input-danger"))
	    }).on("field:validated",
	    function(i) {
	        $(i.$element).hasClass("md-input") && altair_md.update_input($(i.$element))
	    })
	}
		    ,
    init: function() {
        var i = function() {
            $login_form.show().siblings().hide()
        },
        o = function() {
            $register_form.show().siblings().hide();
            $(".uk-width-medium-1-1",$register_form).addClass("uk-grid-margin");
        },
        n = function() {
            $login_help.show().siblings().hide()
        },
        _ = function() {
            $login_password_reset.show().siblings().hide()
        };
        $("#login_help_show").on("click",
        function(i) {
            i.preventDefault(),
            altair_md.card_show_hide($login_card, void 0, n, void 0)
        }),
        $("#signup_form_show").on("click",
        function(i) {
            i.preventDefault(),
            $(this).fadeOut("280"),
            altair_md.card_show_hide($login_card, void 0, o, void 0)
        }),
        $(".back_to_login").on("click",
        function(o) {
            o.preventDefault(),
            $("#signup_form_show").fadeIn("280"),
            altair_md.card_show_hide($login_card, void 0, i, void 0)
        }),
        $("#password_reset_show").on("click",
        function(i) {
            i.preventDefault(),
            altair_md.card_show_hide($login_card, void 0, _, void 0)
        })
    }
};

